"""
It's an enum.  In python.
Don't think too hard about it.
"""

# Don't import anything from kaiten here

class Enum(object):
    def __init__(self, name, dct):
        self.name = name
        self._dict = dct
    def __iter__(self):
        return iter(self._dict.keys())
    def __getattr__(self, attr):
        try:
            return self._dict[attr]
        except KeyError as e:
            raise AttributeError('Enum %s has no attribute %s'% (self.name, e))
    def __getitem__(self, key):
        return self._dict[key]
    def extend(self, *args, **kwargs):
        iterable = ((v, v) for v in args)
        dct = dict(iterable, **kwargs)
        self._dict.update(dct)

def enum(name, *args, **kwargs):
    iterable = ((v, v) for v in args)
    dct = dict(iterable, **kwargs)
    return Enum(name, dct)
